import React from 'react'
//import GoogleLogin from 'react-google-login'
import { GoogleOAuthProvider, useGoogleLogin } from '@react-oauth/google'
import { GoogleLogin } from '@react-oauth/google'
import { useNavigate } from 'react-router-dom'
import { FcGoogle } from 'react-icons/fc'
import shareVideo from '../assets/snaps.mp4'
import logo from '../assets/logowhite.png'
import { client } from '../client'

const Login = () => {
  const navigate = useNavigate()

  // const login = useGoogleLogin({
  //   onSuccess: (tokenResponse) => console.log(tokenResponse),
  // })
  const responseGoogle = useGoogleLogin({
    onSuccess: (tokenResponse) => {
      localStorage.setItem('user', JSON.stringify(tokenResponse.profileObj))
      const { name, googleId, imageUrl } = tokenResponse.profileObj
      const doc = {
        _id: googleId,
        _type: 'user',
        userName: name,
        image: imageUrl,
      }
      client.createIfNotExists(doc).then(() => {
        navigate('/', { replace: true })
      })
    },
  })
  // const responseGoogle = (response) => {
  //   localStorage.setItem('user', JSON.stringify(response.profileObj))
  //   const { name, googleId, imageUrl } = response.profileObj
  //   const doc = {
  //     _id: googleId,
  //     _type: 'user',
  //     userName: name,
  //     image: imageUrl,
  //   }
  //   client.createIfNotExists(doc).then(() => {
  //     navigate('/', { replace: true })
  //   })
  // }

  return (
    <div className='flex justify-start items-center flex-col h-screen'>
      <div className='relative w-full h-full'>
        <video
          src={shareVideo}
          type='video/mp4'
          loop
          controls={false}
          muted
          autoPlay
          className='w-full h-full object-cover'
        />
        <div className='absolute flex flex-col justify-center items-center top-0 right-0 left-0 bottom-0 bg-blackOverlay'>
          <div className='p-5'>
            <img src={logo} width='130px' alt='logo' />
          </div>
          <div className='shadow-2xl'>
            <button
              type='button'
              className='bg-mainColor flex justify-center items-center p-3 rounded-lg cursor-pointer outline-none'
              onClick={() => responseGoogle()}
            >
              <FcGoogle className='mr-4' /> Sing in with Google
            </button>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Login
