export const userQuery = (userId) => {
  const query = `*[_type == 'user' && _id == '${userId}' ]`
  return query
}

export const searchQuery = (searchTerm) => {
  const query = `*[_type == 'snap' && title match '${searchTerm}' || category match '${searchTerm}' about match '${searchTerm}']`
  return query
}
